--
-- Cấu trúc bảng cho bảng `tbltourpackages`
--

CREATE TABLE `tbltourpackages` (
  `PackageId` int(11) NOT NULL,
  `PackageName` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL,
  `PackageType` varchar(150) COLLATE utf8mb4_unicode_ci NOT NULL,
  `PackageLocation` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `PackagePrice` int(11) NOT NULL,
  `PackageFetures` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `PackageDetails` mediumtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `PackageImage` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `Creationdate` timestamp NOT NULL DEFAULT current_timestamp(),
  `UpdationDate` timestamp NULL DEFAULT '0000-00-00 00:00:00' ON UPDATE current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `tbltourpackages`
--

INSERT INTO `tbltourpackages` (`PackageId`, `PackageName`, `PackageType`, `PackageLocation`, `PackagePrice`, `PackageFetures`, `PackageDetails`, `PackageImage`, `Creationdate`, `UpdationDate`) VALUES
(7, 'Tour du lịch cao cấp Đà Nẵng', 'Couple', 'Huế - Quảng Bình - Đà Nẵng - Bà Nà - Hội An', 1000000, 'Những trải nghiệm thú vị trong chương trình - Chiêm ngưỡng khung cảnh thiên nhiên ngoạn mục trên Cây Cầu Vàng.  - Tham quan Bà Nà Hill, Bán Đảo Sơn Trà, Ngũ Hành Sơn, Phố Cổ Hội An, Kinh Thành Huế, Chùa Thiên Mụ.  - Tham quan Động Phong Nha, Thánh Địa La ', 'Sáng: 04:30 Nhân Viên Công ty sẽ đón quý khách tai sân bay Tân Sơn Nhất,  làm thủ tục khởi hành đi Huế trên chuyến bay VJ302 lúc 06:40.\r\n\r\n08:00 Đến Huế , Xe và HDV đón đoàn đến nhà hàng dùng điểm tâm sáng (chi phí tự túc).\r\n\r\n09:00 Đoàn đi tham quan Chùa Thiên Mụ, một ngôi  chùa cổ nằm trên đồi Hà Khê, tả ngạn sông Hương. Chùa Thiên Mụ - là một trong những thắng cảnh đẹp được nhắc đến nhiều nhất tại Huế. Không chỉ đi vào những bài thơ lãng mạn của xứ Huế mà còn trong cả câu ca, Thiên Mụ cũng được coi là một ngôi chùa linh thiêng và những cảnh không thể bỏ qua của bất cứ du khách nào khi đặt chân đến thủ đô cổ xưa này.\r\nTrưa: Xe đưa đoàn đến nhà hàng dùng cơm trưa.\r\n\r\nSau đó đoàn khởi hành về khách sạn 4* nhận phòng nghỉ  ngơi.\r\n\r\nChiều : Đoàn tham quan Đại Nội - Kinh Thành Hoàng Cung của 13 vị Vua triều Nguyễn với Ngọ Môn, điện Thái Hoà, Tử Cấm Thành, Thế Miếu, Hiển Lâm Các, Cửu Đỉnh\r\n\r\nĐây cũng là một trong những di sản của Việt Nam được UNESCO công nhận là Di sản văn hóa thế giới.\r\nTiếp đến, đoàn ghé tham quan chợ Đông Ba - Chợ lớn nhất taị Huế.\r\n\r\nTối: Ăn tối tại Nhà Hàng.\r\n\r\nQuý khách tự do khám phá thành phố Huế về đêm. Nghỉ đêm tại Huế.', 'angelababy-JPG_680x0.jpg', '2022-06-12 08:17:33', '2022-08-04 02:01:45'),
(8, 'Tour Cao Cấp Miền Trung', 'Tour gia đình/tour couple', 'Quy Nhơn - Phú Yên Kỳ Thú', 1200000, 'Những trải nghiệm thú vị trong chương trình Nổi bật lên giữa miền Trung đầy nắng và gió, Phú Yên - Quy Nhơn thu hút du khách bởi...  - Tham quan tháp Đôi – Ngôi tháp Chăm cổ có từ thế kỷ XII trong lòng thành phố.  - Tìm hiểu tranh nghệ thuật bút lửa và cu', 'CHUYẾN BAY DỰ KIẾN:\r\nVN1392T SGNUIH 0740 0855\r\nVN1661T TBBSGN 1605 1720\r\n\r\nNGÀY 01: SÀI GÒN – QUY NHƠN ( ĂN TRƯA, TỐI)\r\n05h40: HDV đón đoàn tại sân bay Tân Sơn Nhất, làm các thủ tục cho đoàn đáp chuyến bay của Vietnam Airline/Bamboo (Sài Gòn – Quy Nhơn) Lúc 7h40.\r\n\r\n09h00: HDV đón Quý khách tại sân bay Phù Cát, đưa đoàn về tham quan:\r\n\r\n- Chùa Thiên Hưng – ngôi chùa nổi tiếng và có quy mô rộng lớn nhất tại Bình Định. Quý khách dâng hương, chiêm bái tại chùa.\r\n- Thăm quan Tây Sơn, đến làng Kiên Mỹ (Tây Sơn) – quê hương của 3 anh hùng áo vải cờ đào Nguyễn Nhạc, Nguyễn Huệ, Nguyễn Lữ.\r\n\r\nĐến nơi đây quý khách bồi hồi như sống lại với khí phách hào hùng của lịch sử cách đây hơn 200 năm của chiến thắng « Ngọc Hồi – Đống Đa » lừng lẫy. Đoàn tham quan các di tích còn xót lại như: cây Me, giếng nước, bến Trầu, tham gia chương trình lễ hội đặc sắc « lễ hội Đống Đa » với phần nghi lễ trang trọng và phần hội là những trò chơi dân gian mang đậm bản sắc miền đất võ như: đấu cờ người, chọi gà, trống trận và võ thuật cổ truyền Bình Định.\r\n11h30: Quý khách dùng bữa trưa tại nhà hàng. Đoàn về nhận phòng khách sạn nghỉ ngơi.\r\n\r\n14h00: Xe và hướng dẫn viên đưa đoàn đi tham quan:\r\n\r\n- Tháp Đôi – Ngôi tháp Chăm cổ có từ thế kỷ XII trong lòng thành phố.\r\n\r\n- Cầu Thị Nại – cầu vượt biển dài nhất Việt Nam nối trung tâm thành phố Quy Nhơn với Khu Kinh Tế Nhơn Hội và bán đảo Phương Mai.\r\n\r\n15h00: Tiếp tục đến tham quan khu du lịch Gềnh Ráng – Tiên Sa với: đồi thi nhân Hàn Mặc Tử, dốc Mộng Cầm, bãi tắm Hoàng Hậu. Tìm hiểu tranh nghệ thuật bút lửa và cuộc đời của nhà thơ tài danh Hàn Mặc Tử cùng những nàng thơ của ông.\r\n19h00: Quý khách dùng bữa tối tại nhà hàng với hải sản biển Quy Nhơn, sau đó quay trở về trung tâm thành phố. Tự do nghỉ ngơi và khám phá thành phố biển về đêm.\r\n\r\nNGÀY 02: TÂY SƠN - KỲ CO - EO GIÓ  - TUY HÒA ( ĂN SÁNG, TRƯA)\r\n06h30: Quý khách dùng điểm tâm sáng tại khách sạn.\r\n\r\n07h30: Xe đưa đoàn di chuyển đi Nhơn Lý, đoàn bắt đầu hành trình:\r\n\r\n- Chinh phục đồi cát Nhơn Lý - lớn nhất Việt Nam – nơi được coi là « tiểu sa mạc giữa lòng biển xanh. Quý khách có thể tham gia trượt cát đầy thú vị nơi đây.\r\n\r\n- Eo gió Nhơn Lý – nơi đất trời, thiên nhiên hội tụ.\r\n- Vãn cảnh chùa Phước Sa – và chiêm ngưỡng vẻ đẹp yên bình của làng chài xã đảo\r\n\r\n08h30: Đoàn bắt đầu hành trình lên ca nô cao tốc khám phá đảo Kỳ Co – được mệnh danh là Maldives của Việt Nam với nước biển trong vắt, bãi cắt trắng trải dài đẹp như mộng, với con đường cầu gỗ bắc ra bến tàu đẹp và ảo diệu. Quý khách tắm biển và lặn ngắm san hô ở đảo bé. Tham gia chương trình khám phá những rặng san hô bất tận & thế giới đại dương lung linh, nơi quý khách sẽ lưu lại những hình ảnh không thể nào quên khi một lần đến với Nhơn Lý.\r\n11h00: Đoàn đáp cano cao tốc quay trở về lại đất liền, tắm nước ngọt.\r\n\r\n11h30: Quý khách dùng bữa trưa hải sản với những đặc sản nổi tiếng tại biển Quy Nhơn như: ốc nón, bề bề, ốc móng tay, ốc vũ nữ….\r\n\r\n12h30: Sau bữa trưa, đoàn di chuyển về Tuy Hòa. Di chuyển về khách sạn nhận phòng nghỉ ngơi.\r\n\r\n18h00: Quý khách tự túc dùng bữa tối, thưởng thức đặc sản Phú Yên như: mắt cá ngừ đại dương, cơm gà Phú Yên, bánh canh hẹ, bánh hỏi cháo lòng….. Tự do khám phá Phú Yên về đêm.\r\n\r\nNGÀY 03: MŨI ĐIỆN – GÀNH XÉP – GÀNH ĐÁ ĐÁ ĐĨA NHÀ THỜ MÀNG LĂNG ( ĂN SÁNG, TRƯA, TỐI)\r\n06h30: Quý khách dùng điểm tâm tại nhà hàng.\r\n\r\n07h30: Đoàn khởi hành tham quan hải đăng Mũi Điện, chinh Phục ngọn Hải Đăng Mũi Điện bằng đường bậc thang từ chân núi lên đến đỉnh núi. Quý khách sẽ ngỡ ngàng trước cảnh đẹp của vịnh Vũng Rô khi nhìn từ trên đỉnh núi. Chụp ảnh lưu niệm tại di tích lịch sử Vũng Rô, nơi cặp bến của những chiếc tàu không số và đường Hồ Chí Minh trên biển.', 'dammeo.jpg', '2022-08-04 02:03:25', '0000-00-00 00:00:00'),
(9, 'Tour Cao Cấp Phan Thiết', 'Tour gia đình/tour couple', 'Núi Tà Cú - Bàu Trắng - Đồi Cát Bay', 830000, 'Những trải nghiệm thú vị trong chương trình - Nghỉ ngơi tại Resort 4 Sao (có hồ bơi, bãi biển riêng) – The Sailing Bay Resort  - Ngắm cảnh đồng bằng Hàm Thuận Nam với những vườn thanh long xanh bạt ngàn tại KDL Tà Cú. Trải nghiệm cáp treo và chiêm bái', 'ĐIỂM ĐÓN:\r\n- Điểm đón 1: 05h00 Nhà Văn Hóa Thanh Niên, 04 Phạm Ngọc Thạch, P. Bến Nghé, Q1.\r\n\r\n- Điểm đón 2: 05h20 Cây xăng Comeco, Ngã 4 Hành Xanh, Phường 21, Quận Bình Thạnh.\r\n\r\n- Điểm đón 3: 06h00 Ngã 4 Thủ Đức, P. Hiệp Phú, Quận 9.\r\n\r\n- Điểm đón 4: 06h30 Siêu thị Lotte Mart, Ngã 4 Amata, TP. Biên Hòa, Đồng Nai.\r\n\r\nVà các điểm đón trên đường đoàn di chuyển ngang qua (nhân viên tư vấn sẽ hướng dẫn điểm đón và thời gian cụ thể cho quý khách)\r\n\r\nNGÀY 01: HỒ CHÍ MINH – NÚI TÀ CÚ – PHAN THIẾT – ĐỒI CÁT BAY  ( ĂN SÁNG, TRƯA, TỐI)\r\n05h00: Xe và hướng dẫn viên  đón khách tại điểm hẹn. Quý khách khởi hành về Phan Thiết.\r\n\r\nĐoàn dừng chân ăn sáng tại Nhà hàng khu vực Đồng Nai.\r\n\r\nTrên xe HDV tổ chức các trò chơi vui nhộn như: tìm người bí ẩn, truy tìm báu vật, chiếc nón kỳ cục, hành trình kết nối với nhiều phần quà hấp dẫn và nghe giới thiệu những điểm trên cung đường mà đã đi qua.\r\n\r\n10h30: Đoàn dừng chân tham quan Núi Tà Cú, du khách đi Cáp treo (phí cáp treo tự túc), ngắm cảnh đồng bằng Hàm Thuận Nam với những Vườn Thanh Long xanh bạt ngàn. Quý khách tham quan Chùa Linh Sơn Trường Thọ với Bộ tượng tam thế Phật được tạc bằng gỗ trầm hương trên 100 năm tuổi và chụp hình lưu niệm với Tượng Phật Nhập Niết Bàn lớn nhất Khu Vực, dài 49m, cao 11m. \r\n11h50: Đoàn dùng cơm trưa tại nhà hàng Tà Cú dưới chân núi.\r\n\r\n13h00: Đoàn khởi hành về khu Resort nhận phòng nghỉ ngơi (Quý khách tự do tắm hồ bơi, tắm biển tại resort).\r\n\r\n17h00: Quý khách tham quan Đồi Cát Bay, ngắm hoàng hôn trên đỉnh đồi. Đây cũng là nơi khơi nguồn cảm hứng bất tận của các nhà nhiếp ảnh. Đồi cát muôn hình, muôn vẻ cùng với những hoạt động, sinh sống của người dân trên cát góp phần cho ra đời những tác phẩm đẹp. Ngoài ra quý khách có thể tự do tham quan trò chơi trượt cát và thưởng thức dừa ba nhát, đậu hủ non hoặc món bánh tai vạt ngay trên đồi cát (Chi phí tự túc).\r\n18h30: Xe đưa Quý khách nhà hàng Resort The Sailing Bay. Buổi tối quý khách tự do khám phá thưởng thức hải sản về đêm. Tự do khám phá thành phố biển Phan Thiết về đêm.\r\n\r\nNGÀY 02: KDL BÀU TRẮNG – TP.HCM ( ĂN SÁNG, TRƯA)\r\n06h30: Quý khách dùng Buffet sáng tại resort.\r\n\r\n08h00: Xe đưa quý khách tham quan KDL Bàu Trắng – Nằm giữa những triền cát trắng nên Bàu Bà còn được gọi là Bàu Trắng và ngày nay cũng thường được gọi với cái tên Bàu Sen bởi trong hồ khi vào mùa sen nở, phủ kín cả một vùng hồ. được thiên nhiên ban tặng khiến cho hồ đẹp đến tuyệt vời.Từ trên đồi cát nhìn xuống hồ phẳng lặng, xa xa những đồi cát sẫm màu nhấp nhô lên xuống khiến du khách không khỏi trầm trồ, thán phục. Quý khách có thể chọn lựa chơi các trò chơi mạo hiểm như đi xe địa hình hoặc đi xe Jeep khám phá Bàu Trắng (Chi phí tự túc).\r\n09h30: Quý khách về resort nghỉ ngơi, tắm biển, tắm hồ bơi.  \r\n\r\n11h30: Đoàn làm thủ tục trả phòng. Đoàn dùng cơm trưa tại nhà hàng.\r\n\r\n12h30: Trên đường về xe dừng tại cơ sở sản xuất nước mắm,khô các loại ...., tại đây quý khách có thể mua quà cho người thân và bạn bè.\r\n\r\n18h00: Quý khách về đến TP.HCM, công ty chia tay quý khách hẹn ngày gặp lại! Kết thúc chuyến tham quan!', '290251586_5605633589469476_4308290758766317957_n.jpg', '2022-08-04 02:06:35', '0000-00-00 00:00:00'),
(10, 'Tour Cao Cấp Vũng Tàu', 'Tour gia đình/tour couple', 'Hồ Tràm - Đồng Cừu - Bình Châu', 1350000, 'Những trải nghiệm thú vị trong chương trình - Vui Chơi Tropicana Park  - Thư Giãn Tại Minera Hot Springs  - Check in tại Đồng Cừu  - Resort 4* Vietsovpetro Cao Cấp.', 'KHUYẾN MÃI: TẶNG VÉ TROPICANA CÁC NGÀY KHỞI HÀNH TRONG TUẦN.\r\nNGÀY 1: TPHCM - HỒ TRÀM - VIETSOVPETRO ( ĂN TRƯA, TỐI)\r\nĐiểm đón: Nhà Văn Hóa Thanh Niên ( số 4,Phạm Ngọc Thạch, Quận 1)\r\n\r\n07h30: Xe và HDV, đón quý khách tại điểm hẹn, khởi hành đi Hồ Tràm. Qúy khách cùng HDV giao lưu, gắn kết các thành viên trong tour. Tham gia các trò chơi tập thể trên xe để nhận phần quà hấp dẫn.\r\n\r\n09h30: Trên đường di chuyển đoàn tham quan tại:\r\n\r\n- Đồng Cừu Suối Nghệ: là một trong những địa điểm rất hot với các bạn trẻ yêu thích chụp ảnh gần đây, quý khách có thể tìm hiểu về nghề nuôi cừu và quy trình nuôi cừu tại địa phương.\r\nTrưa: Đến nhà hàng dùng bữa trưa, tiếp tục lộ trình.\r\n\r\n14h00: Đến với khu Resort Vietsovpetro, nhận phòng nghỉ ngơi. Quý khách tự do tắm biển, hồ bơi và sử dụng các dịch vụ cao cấp tại đây: Hồ Bơi, bãi biển, dù lượn, Moto nước,… hoặc thưởng thức hải sản (chi phí hải sản tự túc). Với quan cảnh thơ mộng, tài nguyên thiên nhiên phong phú, đặc biệt hệ thống bể bơi lớn nhất khu vực Hồ Tràm.\r\n\r\nHoặc lựa chọn thêm điểm tham quan:\r\n\r\n- Công Viên Giải Trí Tropicana Park ( chi phí tự túc 200.000/khách) lấy cảm hứng từ văn hóa tộc người Polynesian ở Thái Bình Dương. Ấn tượng đầu tiên về Tropicana Park là bối cảnh các trò chơi có bối cảnh mang đậm sắc màu đảo Hawaii.\r\nBao gồm các hạng mục như:\r\n\r\n- Công viên nước\r\n\r\n- Núi lửa phun trào\r\n\r\n- Thử thách Maiu\r\n\r\n- Đĩa xoay khổng lồ\r\n\r\n- Tháp xoay cảm giác mạnh\r\n\r\n- Vượt thác Kahuna\r\n\r\n- Chiến thần KuKa, làng Aloha....\r\n\r\nNgoài ra còn các khu mua sắm, ăn uống phục vụ nhu cầu cho tất cả các du khách đến đây vui chơi và trải nghiệm\r\n\r\nTối: Đoàn dùng cơm tối tại Nhà hàng. Tự do nghỉ ngơi tại Vietsovpetro Hồ Tràm.\r\n\r\nNGÀY 02: HỒ TRÀM - TP.HCM ( ĂN SÁNG, TRƯA)\r\nSáng: Quý khách dùng điểm tâm tại resort.\r\n\r\nTự do tắm biển, hồ bơi và sử dụng các dịch vụ cao cấp tại đây: Hồ Bơi, bãi biển, dù lượn, Moto nước,… hoặc thưởng thức hải sản (chi phí hải sản tự túc).\r\n\r\nHoặc lựa chọn thêm dịch vụ tại:\r\n\r\n- Minera Hot Springs Bình Châu ( chi phí tự túc từ 250.000 - 1.800.000/khách) có 2 khu trải nghiệm dịch vụ:\r\n\r\n- Minera Springs với hơn 30 các dịch vụ ngâm tắm khoáng nóng, hồ bơi, spa, các liệu pháp xông nóng - lạnh và dịch vụ ẩm thực vô cùng hấp dẫn. Cảm nhận cơ thể nhẹ hơn, làn da trở nên căng mịn và những đau nhức trên cơ thể dần dần dịu bớt khi đắm mình trong làn nước mát.\r\n- Công viên rừng Minera Forest chỉ cách khu suối khoáng nóng vài bước chân, khu rừng rộng lớn xanh mát hiện ra với những tán cây dọc lối đi, những con đường hình thành từ những bụi tre bí ẩn. Từ ao câu cá đến công viên, từ khu cắm trại đến vườn thú thu nhỏ, Minera Forest là một trải nghiệm riêng đầy kỳ thú. \r\n11h00: Quý khách làm thủ tục trả phòng, Đoàn dùng cơm trưa tại nhà hàng. Sau đó, khởi hành về lại TP Hồ Chí Minh.\r\n\r\nChiều: Trên đường, quý khách dừng chân tham quan và mua đặc sản tại Trung tâm thương mại Bà Rịa - Siêu thị mắm Trí Hải. Tiếp tục lộ trình về Sài Gòn, trên đường tham quan:\r\n\r\n- Chùa Đại Tòng Lâm: hay còn gọi là Vạn Phật Quang Đại Tòng Lâm Tự; là một ngôi đại tự có nhiều công trình quy mô và hiện đại, tiến đến mở Phật học viện, quy tụ Tăng Ni khắp nơi về tu học, đào tạo lực lượng kế thừa thực hiện sự nghiệp hoằng pháp độ sanh.\r\nTiếp Tục lộ trình, quý khách ghé dùng chân và mua sắm đặc sản tại Bò Sữa Long Thành.                                                                 \r\n\r\n18h00: Xe đưa đoàn về điểm đón ban đầu, kết thúc chuyến tham quan. HDV chia tay và hẹn gặp lại Quý khách.\r\n\r\n', 'bia.webp', '2022-08-04 02:33:21', '0000-00-00 00:00:00'),
(11, 'Tour Hà Giang', 'Tour gia đình/tour couple', 'Hà Nội - Hà Giang - Yên Minh - Đồng Văn', 1200000, 'Những trải nghiệm thú vị trong chương trình Chuyến tour đầy lãng mạn và thoải mái bới:  - Chèo thuyền trên sông Nho Quế chạy dọc sườn núi.   - Chợ phiên Đồng Văn đậm nét văn hóa dân tộc với nhiều đồ dân tộc đặc sắc.   - Ngắm những cung đường đèo hùng vĩ. ', '\r\nNGÀY 01: HÀ NỘI – CỘT MỐC SỐ 0 - QUẢN BẠ - YÊN MINH ( ĂN TRƯA, TỐI)\r\n05h30: Xe và Hướng dẫn viên đón Quý khách tại Nhà Hát Lớn Hà Nội. Khởi hành đi Hà Giang - vùng đất có chè san, rượu mật ong và thắng cố, xứ sở của đào phai, hoa lê, truyền thống và náo nhiệt trong buổi chợ phiên…\r\n\r\nTrên đường, Quý khách có thể tranh thủ ngắm cảnh rừng núi Đông bắc vô cùng hùng vĩ và hoang sơ. Và dọc đường đi, xe sẽ dừng nghỉ ngơi để quý khách dùng bữa sáng. (chi phí tự túc).\r\n\r\n11h00: Đoàn checkin Cột mốc số 0 Hà Giang ngay trung tâm thành phố Hà Giang, trong một công viên bên bờ sông Lô.\r\n\r\n12h00: Quý khách đến Tp. Hà Giang, dùng bữa trưa tại nhà hàng thưởng thức đặc sản miền núi.\r\nSau đó quý khách lên đường đi Quản Bạ - Quý khách đến đây đều có dịp chiêm ngưỡng Núi Đôi Cô Tiên - “tác phẩm nghệ thuật” của tạo hóa ban tặng cho vùng đất này, ngoài ra Quý khách còn có dịp nghe kể về truyền thuyết của ngọn núi này, vô vùng hấp dẫn và thú vị.\r\n\r\nTiếp tục hành trình quý khách sẽ đến với rừng thông Yên Minh rừng thông bạt ngàn đẹp nhất Việt Nam trải dài trên các sườn núi cao để đến với đến thị trấn Yên Minh. Quý khách tự do thăm quan và chụp ảnh.\r\n17h30: Đến thị trấn Yên Minh quý khách đến khách sạn nhận phòng khách sạn nghỉ ngơi.\r\n\r\n18h30: Quý khách dùng bữa tối tại nhà hàng. Sau bữa tối quý khách tự do khám phá thị trấn Yên Minh về đêm. Nghỉ đêm tại khách sạn.\r\n\r\nNGÀY 02: YÊN MINH – ĐỒNG VĂN – LŨNG CÚ ( ĂN SÁNG, TRƯA, TỐI)\r\nSáng: Sau bữa sáng quý khách làm thủ tục trả phòng khách sạn tiếp tục khởi hành đi Cao nguyên Đồng Văn - là một cao nguyên đá trải rộng trên 4 huyện Quản Bạ, Yên Minh, Đồng Văn, Mèo Vạc thăm quan:\r\n\r\n- Phố Cáo, Sủng Là, vào làng văn hóa Lũng Cẩm: Nơi lấy bối cảnh những thước phim nổi tiếng “Chuyện của Pao” – vào mùa đông, mùa xuân nở rộ những đồng hoa cải vàng hoặc hoa đào tết, quá thích hợp cho những thước hình lãng mạn mà đậm nét văn hóa…\r\n\r\n- Dinh thự “Vua Mèo” - Dinh thự có tuổi đời trên 100 năm tuổi mô phỏng kiến trúc thành quách của nhà Thanh Trung Hoa, kết hợp với các hoa văn của người Mông và chọn lọc với kiến trúc Pháp. Khu dinh thự của vua Mèo (Dinh thự họ Vương, hay còn được gọi với tên Nhà Vương) tọa lạc trong một thung lũng thuộc địa bàn xã Xà Phìn, huyện Đồng Văn, tỉnh Hà Giang. Dinh thự vua Mèo Vương Chính Đức ở Đồng Văn, Hà Giang là một công trình với kiến trúc đặc sắc bậc nhất của tỉnh Hà Giang, được Nhà nước xếp\r\nhạng di tích kiến trúc nghệ thuật cấp quốc gia.\r\n12h00: Quý khách dùng bữa trưa tại nhà hàng.\r\n\r\n- Cột cờ Lũng Cú - Nơi địa đầu Tổ Quốc, hay còn được miêu tả là: “Nơi cúi mặt sát đất, ngẩng mặt đụng trời”. Quý khách thăm quan Cột cờ Tổ Quốc và chụp hình lưu niệm. Từ cột cờ Lũng Cú, Quý khách có thể ngắm phong cảnh ruộng bậc thang đẹp mắt xen kẽ những nhà trình tường của dân tộc Lô Lô trong bản Séo Lủng bên dưới.\r\n- Mã Pì Lèng - Sông Nho quế: Nơi được mệnh danh là đệ nhất hùng quan của Việt Nam. Quý khách cùng trải nghiệm hành trình Chèo thuyền trên sông Nho Quế chạy dọc sườn núi. Ngồi trên thuyền, ngược dòng nước, ngắm nhìn dòng sông trong vắt uốn lượn quanh triền núi, cảnh tượng quyến rũ biết bao. Dòng sông dịu dàng, giống như dải lụa khổng lồ uốn quanh tầng địa chất đặc biệt nơi hẻm sâu Tu Sản và đèo Mã Pí Lèng. Một khung cảnh vừa tráng lệ, lại rất thơ và tình. Sau đó về khách sạn làm thủ tục nhận phòng.\r\n18h00: Quý khách dùng bữa tối tại nhà hàng. Tự do khám phá ẩm thực phố cổ Đồng Văn về đêm. Quý khách nghỉ đêm tại khách sạn thị trấn Đồng Văn.\r\n\r\nNGÀY 3: ĐỒNG VĂN - MÃ PI LÈNG - HÀ NỘI ( ĂN SÁNG, TRƯA)\r\nSáng: Quý khách dùng bữa sáng và tham gia chợ phiên Đồng Văn đậm nét văn hóa dân tộc với nhiều đồ dân tộc đặc sắc. Sau đó quý khách về khách sạn làm thủ tục trả phòng khách sạn khởi hành về Hà Nội.\r\n\r\n12h00: Quý khách dùng bữa trưa tại nhà hàng tại thành phố Hà Giang sau đó tiếp lên xe về lại Hà Nội.\r\n\r\n18h30: Về đến Hà Nội kết thúc chương trình Hà Giang. Hẹn gặp lại quý khách trong những chương trình tour tiếp theo.\r\n\r\n20h00: Quý khách về tới Hà Nội, chia tay Quý khách và hẹn gặp lại chương trình lần sau.', 'e5d777bbabd574d6a974365bf585f383.jpg', '2022-08-04 02:35:23', '0000-00-00 00:00:00'),
(12, 'Tour Thám Hiểm Phong Nha', 'Tour gia đình/tour couple', 'Thử Sức Hang Tú Làn', 1450000, 'Chuyến Khám phá Tú Làn 2 ngày/1 đêm được đánh giá dễ cho những người năng động, và vừa phải cho những người ít hoạt động thể thao ngoài trời. Bạn phải có chút kinh nghiệm đi trek và biết bơi. Hãy đảm bảo thể lực của bạn phù hợp với các hoạt động sau cho c', 'Khởi hành: 7:30 – 8:00 sáng, từ các khách sạn ở Phong Nha.\r\n\r\nKết thúc: 6:00– 6:30 chiều, tại các khách sạn ở Phong Nha.\r\n\r\nNGÀY 01: PHONG NHA - TÂN HÓA - TÚ LÀN (ĂN TRƯA, TỐI)\r\nChúng tôi sẽ đón bạn tại khách sạn mà bạn đã đăng ký trước trong khu vực Phong Nha - Kẻ Bàng trong khoảng thời gian từ 7h30 đến 8h00. Điểm đến sẽ là văn phòng của công ty tại Tân Hóa.\r\n\r\nSau khi tìm hiểu tất cả thông tin về tính an toàn của chuyến đi, ký vào Phiếu miễn trừ trách nhiệm và hoàn thành công tác thu xếp hành lý, mọi người sẽ bắt đầu cuộc hành trình với việc đi bộ băng qua những cánh đồng ngô và đậu phộng dài 2.5km để đến với sông Rào Nan.\r\nChuyến thám hiểm trở nên gấp rút hơn khi bạn phải leo 30m đường đồi, núi đá lổm chổm, khi bạn nhìn thấy một cửa thung lũng nhỏ, đoàn sẽ dừng chân nghỉ giải lao trước khi đi xuống con dốc dài 100m để đến với thung lũng Hung Ton.\r\nBữa trưa sẽ được phục vụ tại cửa động Hung Ton, sau đó chuyến thám hiểm bắt đầu với những chặng đường khó khăn hơn. Từ thung lũng Hung Ton, vượt qua Núi Xoài và quay lại thung lũng Tú Làn (3 km).\r\nVào buổi chiều, đoàn sẽ thẳng đến khu cắm trại Tú Làn. Các hoạt động buổi chiều bao gồm bơi lội và khám phá Hang Ken, nằm gần bên cạnh địa điểm cắm trại.\r\n\r\n\r\nSau đó trở lại khu cắm trại và thưởng thức bữa tối tuyệt vời đã được chuẩn bị sẵn. Thả mình vào khung cảnh thiên nhiên núi rừng, bạn sẽ chìm vào giấc ngủ một cách dễ dàng. Bạn có thể lựa chọn ngủ võng hoặc lều.\r\n\r\nNGÀY 02: TÚ LÀN - TÂN HÓA - PHONG NHA (ĂN SÁNG, TRƯA) \r\nBữa sáng được chuẩn bị chu đáo, cung cấp đầy đủ năng lượng cho Quý khách.\r\n\r\nHành trình ngày thứ hai bắt đầu bằng một đoạn bơi ngắn để đến với hang Tú Làn, hang động lớn nhất trong hệ thống này. Bạn sẽ được đắm mình tận hưởng tại hồ nước xanh mát trước khi bơi 180m dọc theo hạ lưu sông qua cửa hang nước Tú Làn.\r\nTiếp tục leo trèo, luồn lách qua các mỏm đá để khám phá hang khô Tú Làn.\r\nSau đó bạn sẽ tới lối ra của hang này và đi bộ thêm một khoảng ngắn xuyên qua thung lũng để vào cửa Hang Kim.\r\nTận hưởng thời gian của bạn ở đây để ngắm nhìn giếng trời tự nhiên của hang, sau đó bơi tiếp 450 m và đi bộ thêm 500m nữa để tới thung lũng Tổ Mộ.\r\nBữa trưa được chuẩn bị sẵn sàng tại khu cắm trại, ngay cạnh cửa hang Hung Ton. Sau bữa trưa, bạn sẽ tiếp tục khám phá hệ thống hang khô và hang nước tại đây.\r\nHành trình sẽ kết thúc và trở về lại với thung lung Hung Ton (800m), băng qua sông Rào Nan và những cách đồng nhỏ, cuộc hành trình sẽ kết thúc tại văn phòng công ty Tân Hóa.\r\n\r\nTại văn phòng công ty Tân Hóa, Quý khách có thể thư giãn nghỉ ngơi với nhiều thức uống lạnh. Văn phòng cũng có nhà vệ sinh riêng nếu Quý khách muốn tắm rửa và thay đồ trước khi quay trở lại Phong Nha tầm 5 giờ 30 chiều (khoảng 6 giờ đến 6 giờ 30 trong thời gian từ tháng 11 đến tháng 3).', 'W8Z6l_5_c.jpg', '2022-08-04 02:37:28', '0000-00-00 00:00:00'),
(13, 'Tour Tây Bắc', 'Tour gia đình/tour couple', 'Mộc Châu - Sơn La - Điện Biên - Lai Châu - SaPa', 2000000, 'Những trải nghiệm thú vị trong chương trình Hành trình liên tuyến sẽ đưa quý khách trải nghiệm qua các nẻo đường:  - Chiêm ngưỡng thác Dải Yếm, rừng thông bản Áng, đồi chè hình trái tim, làng hoa cải, hoa mận tại cao nguyên Mộc Châu.  - Thác Bạc – một tro', 'NGÀY 01: HÀ NỘI – MỘC CHÂU – SƠN LA ( ĂN TRƯA, TỐI)\r\nSáng: Xe ô tô và hướng dẫn viên đón quý khách khởi hành đi Mộc Châu, trên đường đi quý khách sẽ dừng chân ăn sáng tại nhà hàng (chi phí tự túc).\r\n\r\nTrên đường đi đoàn dừng chân và nghỉ ngơi và chụp ảnh tại đèo Thung Khe – Hòa Bình hay còn được gọi là đèo Đá Trắng, được dân tình ca ngợi là “ Châu Âu của Hòa Bình”.  Điểm đặc biệt là thời tiết ở đây khá thú vị. Trên đèo Đá Trắng, một ngày bạn có thể được trải nghiệm đủ 4 mùa trong năm. Xe tiếp tục đưa quý khách di chuyển lên thị trấn Mộc Châu. \r\n11h30: Quý khách dùng bữa trưa tại nhà hàng với những món ăn mang đậm hương vị vùng cao, sau bữa trưa quý khách di chuyển về khách sạn nghỉ ngơi.\r\n\r\n14h30: Qúy khách khởi hành đi thăm quan:\r\n\r\n- Đồi chè trái tim: Đây là một trong những khu vực đẹp nhất Mộc Châu. Cho dù vào mùa đông cành khô trơ trụi lá hay mùa xuân mơn mởn khoe sắc lá non, Mộc Châu vẫn luôn được bao bọc trong màu sắc xanh tuyệt đẹp của những đồi chè. Đồi chè đẹp nhất có lẽ là đồi chè trái tim, đồi chè chữ S. \r\n- Thung Lũng mận Nà Ka: Nằm trên đường vào xã Tân Lập, thung lũng mận Nà Ka cách thị trấn Nông trường Mộc Châu 16km. Từ tỉnh lộ 104, rẽ trái vào thung lũng chỉ mất vài phút đi xe máy. Trước mắt bạn sẽ hiện ra thung lũng mận bạt ngàn, bằng phẳng. Hàng năm vào mùa hoa mận nở, thung lũng được phủ một lớp màn trắng lung linh bất tận của hoa mận. (mùa hoa mận nở nhiều vào tháng 12 hàng năm, mùa quả mận tháng 5 hàng năm)\r\n- Thác Dải Yếm: Một thác nước tuy nhỏ nhưng mang một vẻ đẹp quyến rũ. Tương truyền, dòng thác này là dải yếm của người con gái cứu chàng trai thoát khỏi dòng nước lũ. \r\n18h30: Quý khách dùng bữa tối tại nhà hàng.\r\n\r\nNghỉ đêm tại khách sạn tại Mộc Châu.\r\n\r\nNGÀY 02: SƠN LA – ĐIỆN BIÊN ( ĂN SÁNG, TRƯA, TỐI)\r\n07h00: Qúy khách dùng bữa sáng sau đó khởi hành đi Điện Biên, trên đường quý khách có dịp chiêm ngưỡng ngắm cảnh đẹp núi rừng Tây Bắc với các làng Thái ven bờ sông Đà.\r\nTrên đường quý khách sẽ chinh phục đèo Pha Đin - một trong “Tứ Đại Đỉnh Đèo” những đường đèo dài và hiểm trở nhất Việt Nam, ranh giới 2 tỉnh Sơn La và Điện Biên. \r\n\r\n\r\n11h30: Qúy khách dùng cơm trưa tại nhà hàng.\r\n\r\n14h30: Qúy khách đến Điện Biên quý khách tham quan Sở chỉ huy chiến dịch Điện Biên Phủ, hầm Đại Tướng Võ Nguyên Giáp, Tham mưu trưởng Hoàng Văn Thái và Bộ chỉ huy chiến dịch năm xưa.\r\n\r\nQúy khách tham quan Bảo Tàng Điện Biên Phủ nơi gắn liền với chiến thắng Điện Biên Phủ trong lịch sử. \r\n18h30: Quý khách dùng bữa tối tại nhà hàng hoặc quý khách có thể đặt ăn tối và giao lưu văn nghệ tại Bản Mển - bản của người Thái đen (chi phí xem văn nghệ tự túc).\r\n\r\nNghỉ đêm tại khách sạn tại thành phố Điện Biên.\r\n\r\nNGÀY 03: ĐIỆN BIÊN - LAI CHÂU - SAPA ( ĂN SÁNG, TRƯA, CHIỀU)\r\n07h00: Sau bữa sáng quý khách khởi hành đi thăm quan các di tích gắn liền với chiến thắng Điện Biên Phủ lịch sử:  Đồi A1, Hầm Đờ Cát, Cầu Mường Thanh, sông Nậm Rốn, Tượng đài chiến thắng Điện Biên Phủ - Những địa danh vẫn còn mang đầy dư âm oai hùng của cuộc chiến mang ý nghĩa lịch sử của toàn dân tộc. \r\nSau đó khởi hành đi Sapa đoàn sẽ đi ngang qua các địa danh: Mường Chà, Mường Lay, cầu Hang Tôm - ranh giới 2 tỉnh Điện Biên & Lai Châu, Phong Thổ,... Trên đường đi, đoàn có dịp chiêm ngưỡng khung cảnh hùng vĩ của núi rừng Tây Bắc.\r\n12h00: Qúy khách dùng bữa trưa tại nhà hàng.\r\n\r\nSau bữa trưa quý khách tiếp tục khởi hành về Sapa chinh phục dãy Hoàng Liên Sơn, chụp hình lưu niệm tại đỉnh đèo Ô Quy Hồ - một trong tứ đại đỉnh đèo của Việt Nam, ngắm cổng trời Ô Quy Hồ, sau đó đoàn tiếp tục khởi hành đi tham quan Thác Bạc – một trong những thác nước đẹp nhất Việt Nam.\r\n17h00: Qúy khách về nhận phòng khách sạn nghỉ ngơi, dùng bữa tối. Buổi tối tự do thăm quan chụp hình Sapa nhộn nhịp về đêm. Ngồi với nhau và thưởng thức những món ngon đặc sản nướng của Sapa. Vào các tối thứ 7, quý khách còn được thưởng thức văn nghệ truyền thống tại sân chợ tình Sapa.\r\n\r\nNghỉ đêm tại khách sạn thị trấn Sapa.\r\n\r\nNGÀY 04: SAPA – HÀ NỘI ( ĂN SÁNG, TRƯA)\r\n07h00: Sau bữa sán quý khách khởi hành đi thăm quan Khu du lịch Swing Sapa hướng về phía núi, càng trông như một bức tranh sơn dầu, chính vì thế mà nó được ví von như một Đà Lạt thu nhỏ giữa lòng Lào Cai.\r\n\r\nTiếp đến, những tiêu cảnh cực ấn tượng như tượng Phật cổ kính, sẽ khiến người ta như lạc ngoài đảo hoang Bali, hay check-in bên quán cafe view phố núi, rồi chill cùng những bản nhạc núi rừng, thưởng ly đồ uống nóng thư thái, ngắm nhìn biển mây lãng đãng đẹp đến nao lòng. \r\nSau đó, quý khách di chuyển về khu du lịch cáp treo Fansipan với hệ thống cáp treo 3 dây hiện đại vừa khánh thành tháng 2/2016. Trải nghiệm cảm giác đi giữa biển mây. Viếng khu tâm linh Fanpsian, vượt gần 600 bậc thang, chinh phục “Nóc nhà Đông Dương” - đỉnh Fansipan 3,143m. (chi phí tự túc)\r\n\r\n\r\n12h00: Qúy khách dùng bữa trưa tại nhà hàng. Sau bữa trưa quý khách lên xe khởi hành về lại Hà Nội.\r\n\r\n18h00: Về đến sân bay Nội Bài - Hà Nội chia tay quý khách hẹn gặp lại quý khách vào những chương trình tiếp theo. ', 'con-tho-dang-nguoi-bi-danh-vao-tay-hu-nay-0fef92b0b9547eca615f9a445aa2d95d.jpg', '2022-08-04 02:41:40', '0000-00-00 00:00:00'),
(14, 'Tour Miền Bắc', 'Tour gia đình/tour couple', 'Hà Nội - Ninh Bình - Hạ Long', 560000, 'Những trải nghiệm thú vị trong chương trình Nhắc đến xứ Bắc, không thể bỏ lỡ:  - Vịnh Hạ Long: Theo CNN, là điểm du lịch đẹp nhất tại Việt Nam, đồng thời là một trong những điểm tham quan tự nhiên nổi tiếng của Đông Nam Á. Với 1.969 đảo đá vôi cùng những ', 'NGÀY 01: ĐÓN SÂN BAY NỘI BÀI – HÀ NỘI \r\nXe đón Quý Khách tại sân bay Nội Bài đưa về Khách sạn nhận phòng, nghỉ ngơi.\r\n\r\nQuý khách tự do khám phá thủ đô Hà Nội với 36 Phố Phường, tham quan tháp rùa, cầu Thê Húc, chùa Trấn Quốc, Lăng Bác, Văn Miếu Quốc Tử Giám,… \r\n\r\nBuổi tối: Quý khách tự do thưởng thức ẩm thực đường phố mang nét văn hóa của Hà Nội xưa như chả cá Lã Vọng, bún Thang, phở Lý Quốc Sư, phở cuốn Ngũ Xá,….\r\n\r\nNgủ đêm tại Khách sạn 3 sao phố cổ Hà Nội. \r\n\r\nNGÀY 02: HÀ NỘI - BÁI ĐÍNH – TRÀNG AN – HÀ NỘI (ĂN SÁNG, TRƯA)\r\nBuổi Sáng: Qúy khách, ăn sáng khởi hành đi Ninh Bình. Tới Ninh Bình, Qúy khách viếng và thăm quan chùa Bái Đính với tượng Phật Thích ca bằng đồng cao và nặng nhất Viêt Nam, Chùa có bộ Tam Thế bằng đồng lớn nhất Việt Nam, Chùa có nhiều tượng La Hán nhất Việt Nam…\r\n\r\nQúy khách quay trở lại nhà hàng ăn trưa với các món đặc sản thịt dê…\r\nChiều: Qúy khách tiếp tục thăm quan khu du lịch Tràng An, đoàn xuống thuyền du ngoạn khám phá vẻ đẹp hoang sơ được ví như Vịnh Hạ Long trên cạn với vô vàn các hạng động bằng những con thuyền nhỏ. Qúy khách ghé thăm và dâng hương lên thần thánh tại các ngôi đền thờ ở hai bên dòng sông của khu du lịch Tràng An.\r\nVề đến Hà Nội, xe đưa Quý Khách về lại Khách sạn, tự do ăn tối, khám phá Phố Cổ nhộn nhịp, đi chợ đêm Đồng Xuân, dạo chơi phố đi bộ,…\r\n\r\nNGÀY 03: HÀ NỘI - VỊNH HẠ LONG - CHÈO THUYỀN KAYAK (ĂN SÁNG, TRƯA, TỐI)\r\nSáng: Xe đón quý khách tại khách sạn, sau đó khởi hành đi Hạ Long. Đến cảng tàu Hạ Long, Đoàn tập trung tại nhà chờ theo sự hướng dẫn của hướng dẫn viên đợi mua vé và phát vé cho Quý khách để lên tàu bắt đầu hành trình khám phá Vịnh. Tàu sẽ đi qua rất nhiều các hòn đảo lớn nhỏ với nhiều hình dáng khác nhau có những tên gọi đặc biệt như: Hòn Gà Chọi, hòn Chó Đá, hòn Đỉnh Hương, hòn Ngón Tay...\r\nQuý khách thưởng thức bữa trưa trên tàu. \r\n\r\nChiều: Đến đảo Bồ Hòn. Quý khách thăm quan Hang Sửng Sốt - Một trong những hang động đẹp nhất vịnh Hạ Long. Tiếp theo, Quý khách sẽ chèo Kayak hoặc đi thuyền nan thăm Hang Luồn.\r\nSau 30 phút chèo Kayak, Quý khách lên tàu để đến với đảo Ti Top. Quý khách có thể tắm biển tại bãi tắm TiTop với bãi cát trắng, hoặc thử treekking leo lên đỉnh núi Ti Top ngắm nhìn toàn cảnh Vịnh Hạ Long.\r\nTàu sẽ di chuyển đưa đoàn về lại Bến. Xe ô tô đón và đưa về khách sạn nhận phòng, nghỉ ngơi.\r\n\r\nĐoàn dùng buổi tối và tự do vui chơi khám phá Chợ Đêm, Phố ẩm thực... Nghỉ đêm tại Hạ Long.\r\n\r\nNGÀY 04: KHÁM PHÁ THÀNH PHỐ HẠ LONG – HÀ NỘI - TIỄN BAY (ĂN SÁNG, TRƯA)\r\nSáng: Ăn sáng tại khách sạn. Buổi sáng Quý khách có thể lựa chọn:\r\n\r\nOpition 1: Tự do vui chơi tại khu Công viên Hạ Long với nhiều khu vui chơi hấp dẫn (chi phí vé vui chơi tại các khu công viên tự túc):\r\n\r\n- Khu Cáp treo Nữ hoàng và vòng quay Sunwheel khổng lồ trên khu đồi huyền bí.\r\n- Công viên Dragon park với các trò chơi mạo hiểm như tàu lượn, đu quay...\r\n\r\n- Công viên Typhoon water park với các trò chơi liên quan đến nước đầy hấp dẫn\r\n\r\nOpition 2: Tự do tắm biễn Bãi Cháy.\r\n11h30: Quý khách trả phòng khách sạn, ăn trưa tại khách sạn. Sau đó lên xe về Hà Nội.\r\n\r\n17h00: Tiễn sân bay cho khách.\r\n\r\n17h15: Về đến Hà Nội. Kết thúc chương trình. Hẹn gặp lại quý khách!\r\n\r\n Lịch trình thăm quan có thể thay đổi nhưng vẫn đảm bảo các điểm thăm quan có trong chương trình.', 'a4bc5c175a7751a471bb29b42aef83c3.jpg', '2022-08-04 02:48:13', '0000-00-00 00:00:00');

